import styles from "./Feed.module.scss";
import { useState, useRef } from "react";
import Comments from "./Comments";

function Feed({ username,feedcontent }) {
  const [userName] = useState('codelike');
  const [commmentArr, setCommentArr] = useState([]);
  const [isValid, setIsValid] = useState(true);
  const [id, setId] = useState(0);
  const ref = useRef();

  const onSubmit = e => {
    e.preventDefault();
    if (ref.current.value.length > 0) {
      setId(id+1);
      let newComment = {
        id: id,
        content: ref.current.value,
      };
      setCommentArr([...commmentArr, newComment]);
      ref.current.value = "";
      setIsValid(true);
    }
  }

  const onChange = () => {
    ref.current.value.length > 0
      ? setIsValid(false)
      : setIsValid(true)
  };

  return (
    <article>
      <div className={styles.feedHead}>
        <div className={styles.profile}>
          <img src="image/Profile.jpg" alt="프로필" />
          <a href="{() => false}">{ username }</a>
        </div>
        <div>
          <img src="image/dots.png" alt="더보기" />
        </div>
      </div>

      <div className={styles.feedBody}>
        <img src="image/feed1.jpg" alt="피드 사진" />
      </div>
      <div className={styles.feedMenu}>
        <div>
          <button>
            <img src="image/heart.png" alt="좋아요" />
          </button>
          <button>
            <img src="image/chat.png" style={{ width: "33px" }} alt="댓글" />
          </button>
          <button>
            <img src="image/send.png" alt="보내기" />
          </button>
        </div>
        <div>
          <button>
            <img src="image/save-instagram.png" alt="저장" />
          </button>
        </div>
      </div>

      <div className={styles.feedComment}>
        <div>
          <span>좋아요 28개</span>
        </div>
        <div>
          <a href="{() => false}">poy.lib</a>
          <span>{feedcontent}</span>
        </div>
        <div>
          {commmentArr.map((props) => (
            <Comments
              key={props.id}
              userId={userName}
              userComment={props.content}
            />
          ))}
        </div>
        <span>28분 전</span>
      </div>
      <div className={styles.feedWrite}>
        <form
        onSubmit={onSubmit}
        >
          <input
            ref={ref}
            onChange={onChange}
            placeholder="댓글 달기..."
          />
          <button
            style={{
              opacity: isValid ? 0.5 : 1
            }}
          >
            게시
          </button>
        </form>
      </div>
    </article>
  );
}
export default Feed;
